import { render } from '@testing-library/react';
import React, { Component } from 'react';
import {Input} from 'reactstrap';

class SearchIndex extends Component{
   constructor () {
     super();
     this.state = {
       filter: "", 
     things: ['pen', 'marker', 'eraser', 'notebook', 'pencil', 'scissors', 'highlighter', 'stapler', 'paper clip', 'binder', 'hole punch', 'laminator', 'laminating sheets', 'protective sheets', 'index cards']
   }
 }

searchTxt(e) {
  this.setState({ filter: e.target.value });
}; 

render(){
  let { filter, data } = this.state;

  let Datasearch=data.filter(item => {
    return Object.keys(item).some(key =>
      item[key].toLowerCase().includes(filter.toLowerCase()))
  })
   return(
   <div>
     <center>
       <h1> School Suppply Search</h1>
       <hr />
       Search : <input type="text" value={filter} onChange={this.searchTxt.bind(this)} placeholder="Search for Lost Supply - Don't Miss the Bus!!"/>
       <hr />
       <table border="1">
         <tr>
           <th>Supply Name</th>
         </tr>
         {Datasearch.map(item =>  //use .filter if .map doesn't work
          <tr>
            <td>
            {item.things}
            </td>
          </tr>
          )}
       </table>
     </center>
   </div>
         )};

}


export default SearchIndex;
