import React, { Component } from 'react';
import {Input} from 'reactstrap';
import Search from './Search'


class SearchIndex extends React.Component {


     //super();
     state = {
       //filter: "", 
     things: ['pen', 'marker', 'eraser', 'notebook', 'pencil', 'scissors', 'highlighter', 'stapler', 'paper clip', 'binder', 'hole punch', 'laminator', 'laminating sheets', 'protective sheets', 'index cards']
   }
   //searchTerm: ''
 //

 editSearchTerm = (e) => {
   this.setState({searchTerm: e.target.value})
 }

 dynamicSearch = () => {
   return this.state.things.filter(thing => thing.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
 }

 render(){
   return(
   <div style = {{textAlign: 'center', paddingTop: '30vh'}}>
     <input type= 'text' value = {this.state.searchTerm} onChange = {this.editSearchTerm} placeholder = 'Search for a Supply!'/>
     <br></br>
     <h3>Don't Miss the Bus!</h3>
     <Search things = {this.dynamicSearch()}/>

   </div>
   )
 }


}


export default SearchIndex;
